mapper.update:
UPDATE tb_business_user SET business_user_type=#{businessUserType} , manage_name=#{manageName}, business_username=#{businessUsername}, legal_people_name=#{legalPeopleName}, legal_people_identity_card=#{legalPeopleIdentityCard}, contact_people=#{contactPeople}, contact_phone=#{contactPhone}, contact_mail=#{contactMail}, service_phone=#{servicePhone}, manage_address=#{manageAddress}, manage_province=#{manageProvince}, manage_city=#{manageCity}, manage_district=#{manageDistrict}, identity_card_front=#{identityCardFront}, identity_card_reverse=#{identityCardReverse}, identity_card_hand=#{identityCardHand}, bank_card_front=#{bankCardFront}
, business_license_no=#{businessLicenseNo}, register_address=#{registerAddress}, business_license_picture=#{businessLicensePicture}, door_picture=#{doorPicture}, register_license_picture=#{registerLicensePicture}
WHERE business_uid=#{businessUid}

mapper.select:
SELECT * FROM tb_business_user b,tb_agent a where b.affiliation_agent = a.agent_number and a.agent_number = #{affiliationAgent}

    and b.business_uid = #{businessUid}


    and b.business_user_type = #{businessUserType}


    and b.manage_name LIKE '%${manageName}%'


    and business_user_status = #{businessUserStatus}


    and b.start_time > #{startTime}


    and b.end_time < #{endTime}


    and legal_people_name like '%${legalPeopleName}%'


    and contact_phone = #{contactPhone}
